  data() {
    return {
      chartOptions: {
        responsive: false,
        plugins: {
          title: {
            display: true,
            text: 'Evolução dos Investimento ao longo do tempo'
          }
        },
        scales: {
          y: {
            title: {
              display: true,
              text: 'Reais',
              color: '#373e98'
            }
          }
        }
      }
    };
  }

};
